import { createReducer, on } from "@ngrx/store";
import { addToCartStatus, updateaddToCartStatus } from "../actions/song-list.actions";
import { initialState } from "../state/song-list.state";
export const songListFeatureKey = "song-list";
export const reducer = createReducer(initialState, on(addToCartStatus, state => Object.assign(Object.assign({}, state), {
  addToCart: "false"
})), on(updateaddToCartStatus, (state, {
  id
}) => Object.assign(Object.assign({}, state), {
  addToCart: "true",
  id: id
})));
export function SongListReducer(state, action) {
  return reducer(state, action);
}
