import { createReducer, on } from "@ngrx/store";
import * as UserActions from "../actions/user.actions";
export const userFeatureKey = "user";
export const initialState = {
  user: "",
  connected: false
};
export const reducer = createReducer(initialState, on(UserActions.connectUser, state => state), on(UserActions.connectUserSuccess, (state, {
  user
}) => Object.assign(Object.assign({}, state), {
  user,
  connected: true
})), on(UserActions.connectUserFailure, (state, action) => state));
export const userAccountId = state => state.user;
export const connectionStatus = state => state.connected;
